<<<"},"$:/plugins/kookma/garden/macros/clone-create":{"title":"$:/plugins/kookma/garden/macros/clone-create","created":"20221021124116863","modified":"20221023200513097","tags":"$:/tags/Macro/Garden","type":"text/vnd.tiddlywiki","text":"\define create-new-actions()
<$let
  new-code-title   = <<unusedtitle template:"$count:3$/code">> 
  new-remark-title = {{{ [<new-code-title>removesuffix[code]addsuffix[remark]] }}} 
  >
<$action-sendmessage $message="tm-new-tiddler" title=<<new-remark-title>> tags="Remark" />
<$action-sendmessage $message="tm-new-tiddler" title=<<new-code-title>> tags="Code"   description="what is this code?" keywords=""/>
</$let>	
\end	

\define create-new-example()
<$button class="tc-btn-invisible" tooltip="create new example" actions=<<create-new-actions>> >
{{$:/core/images/new-button}}
</$button>
\end

\define clone-this-actions()
<$let
  new-code-title      = <<unusedtitle template:"$count:3$/code">> 
  new-remark-title    = {{{ [<new-code-title>removesuffix[code]addsuffix[remark]] }}} 
  new-code-template   = <<__thisTiddler__>>
  new-remark-template = {{{ [<new-code-template>removesuffix[code]addsuffix[remark]] }}}
  >
<$action-sendmessage $message="tm-new-tiddler" $param=<<new-remark-template>> title=<<new-remark-title>>  />	
<$action-sendmessage $message="tm-new-tiddler" $param=<<new-code-template>>   title=<<new-code-title>>    />
</$let>	
\end	


\define clone-this-example(thisTiddler)
<$button class="tc-btn-invisible" tooltip="clone this example" actions=<<clone-this-actions>> >
{{$:/core/images/clone-button}}
</$button>
\end"},"$:/plugins/kookma/garden/macros/main-ui":{"title":"$:/plugins/kookma/garden/macros/main-ui","created":"20210525091619581","modified":"20221024034724440","tags":"$:/tags/Macro/Garden","type":"text/vnd.tiddlywiki","text":"\whitespace trim

\define stateTid()    xx$:/state/garden/examples
\define sandboxTid()  xx$:/sandbox/garden/examples/$(vv-exmp-tiddler)$

\define btn-edit-example()
<$button class="tc-btn-invisible" tooltip="edit code">{{$:/core/images/edit-button}}
<$action-setfield $tiddler=<<sandboxTid>> $field=text $value={{{ [<vv-exmp-tiddler>get[text]] }}}/>
</$button>
\end

\define btn-reset-example()
<$list filter="[<vv-edit-status>match[yes]then[no]else[yes]]" variable=disabled>
<$button class="tc-btn-invisible" disabled=<<disabled>> tooltip="restore original code">{{$:/core/images/refresh-button}}
<$action-deletetiddler $tiddler=<<sandboxTid>>/>
</$button></$list>
\end

\define btn-copy-example-to-clipboard()
<$button message="tm-copy-to-clipboard" class="tc-btn-invisible" tooltip="copy code to clipboard" 
         param={{{ [<sandboxTid>get[text]] :else[<stateTid>get[text]get[text]] }}} >
{{$:/core/images/copy-clipboard}}
</$button>
\end

<!-- create the main ui of garden -->
\define garden-ui(filter:'[all[shadows+tiddlers]tag[Code]sort[title]]')
<$let vv-exmp-filter  = <<__filter__>>
      vv-exmp-tiddler = {{{ [<stateTid>get[text]] }}}
      vv-edit-status  = {{{ [<sandboxTid>is[tiddler]then[yes]else[no]] }}} >

<!-- create the header, to select an example and navigate through them -->
<header class="twg-header">
<$select tiddler=<<stateTid>> default='default' class="twg-select">
<option value='default' disabled>Select an example</option>
<$list filter='[subfilter<vv-exmp-filter>]'>
<option value=<<currentTiddler>>>
<$view field='caption'>
<$view field='title'/>
</$view>
</option>
</$list>
</$select>

<$tiddler tiddler=<<vv-exmp-tiddler>> >
<$macrocall $name=simpleNavigation tag="Code" class="twg-navigation"/>
</$tiddler>
</header>

<!-- the Nav part implements buttons to create/clone/view buttons and edit/reset/copy code-->
<nav class="twg-nav">
<div>
<<create-new-example>> | 
<$macrocall $name="clone-this-example" thisTiddler=<<vv-exmp-tiddler>> /> | 
<$macrocall $name="open-this-in-storyriver" thisTiddler=<<vv-exmp-tiddler>> />
</div>
<div>
<<btn-edit-example>> | <<btn-reset-example>> | <<btn-copy-example-to-clipboard>>
</div>
</nav>

<!-- display the body of code -->
<$tiddler tiddler={{{ [<vv-edit-status>match[yes]then<sandboxTid>else<vv-exmp-tiddler>] }}} >
<$transclude mode=block tiddler="$:/plugins/kookma/garden/templates/editor-viewer"/>
</$tiddler>

<!-- display the remark section of example (remark tiddler) -->
<section class="twg-remarks">
<$transclude tiddler={{{ [<vv-exmp-tiddler>removesuffix[code]addsuffix[remark]]}}} mode=block/>
</section>

</$let>
\end"},"$:/plugins/kookma/garden/macros/open-in-storyriver":{"title":"$:/plugins/kookma/garden/macros/open-in-storyriver","created":"20221021185236605","modified":"20221023192705945","tags":"$:/tags/Macro/Garden","type":"text/vnd.tiddlywiki","text":"\define open-this-in-storyriver(thisTiddler)
<$let
  code-tiddler   = <<__thisTiddler__>>
  remark-tiddler = {{{ [<code-tiddler>removesuffix[code]addsuffix[remark]] }}}
  >
<$button class="tc-btn-invisible" tooltip="open example code and remark in story river" actions='	
  <$action-navigate $to=<<remark-tiddler>> />
  <$action-navigate $to=<<code-tiddler>> $scroll="yes"/>
 '>
{{$:/plugins/kookma/garden/images/open-in-story}}
</$button>
</$let>	
\end"},"$:/plugins/kookma/garden/macros/simpel-navigation":{"title":"$:/plugins/kookma/garden/macros/simpel-navigation","created":"20210526053558301","modified":"20221021141859237","tags":"$:/tags/Macro/Garden","type":"text/vnd.tiddlywiki","text":"\define simpleNavigation(tag:"demo", class:"twg-navigation")
<div class="$class$">
<$list filter="[tag[$tag$]before<currentTiddler>]" variable="prevTiddler">
<$button tooltip=<<prevTiddler>> class="tc-btn-invisible">previous
<$action-setfield $tiddler=<<stateTid>> $field=text $value=<<prevTiddler>> />
</$button>
</$list>
<$list filter="[tag[$tag$]after<currentTiddler>] [tag[$tag$]before<currentTiddler>] +[count[]compare:integer:gteq[2]]" variable=ignore>|</$list>
<$list filter="[tag[$tag$]after<currentTiddler>]" variable="nextTiddler">
<$button tooltip=<<nextTiddler>> class="tc-btn-invisible">next
<$action-setfield $tiddler=<<stateTid>> $field=text $value=<<nextTiddler>> />
</$button>
</$list>
</div>
\end"},"$:/plugins/kookma/garden/readme":{"title":"$:/plugins/kookma/garden/readme","created":"20221024031355708","modified":"20221024050055265","tags":"","type":"text/vnd.tiddlywiki","text":";Tiddlywiki Garden
The garden is used to demonstrate, practice and learn, Filters, Wikitext, and CSS. Tiddlywiki garden ispired from [[CSS Zen Garden|https://en.wikipedia.org/wiki/CSS_Zen_Garden]].

;Code and demo
For learning plugin features, syntax, tutorial and examples see the plugin demo and code pages

* Demo: https://kookma.github.io/TW-Garden/
* Code: https://github.com/kookma/TW-Garden
"},"$:/plugins/kookma/garden/styles/code-tiddler":{"title":"$:/plugins/kookma/garden/styles/code-tiddler","created":"20221023202042099","modified":"20221023202822053","tags":"$:/tags/Stylesheet","type":"text/vnd.tiddlywiki","text":"[data-tags*="elementary"] {
