}","created":"20210526054212988","modified":"20221021043748987","tags":"$:/tags/Stylesheet","type":"text/css"},"$:/plugins/kookma/garden/templates/editor-viewer":{"title":"$:/plugins/kookma/garden/templates/editor-viewer","code-body":"yes","created":"20210525092326172","modified":"20221024034724436","tags":"","type":"text/vnd.tiddlywiki","text":"\define rawCode()
<$edit-text class="twg-wide-edit"	tag=textarea field=text/>
\end

\define lockedCode()
<$codeblock code={{!!text}} language=xml />
\end

\define renCode()
<$transclude mode=block/>
\end

<section class="twg-grid-container">
    <div class="twg-grid-column twg-grid-code">
        <$list filter="[<vv-edit-status>match[yes]]" variable=null emptyMessage=<<lockedCode>> ><<rawCode>></$list>
    </div>	
    <div class="twg-grid-column twg-grid-rendered">
        <<renCode>>
    </div>
</section>"},"$:/plugins/kookma/garden/viewtemplates/tiddler-code":{"title":"$:/plugins/kookma/garden/viewtemplates/tiddler-code","code-body":"yes","created":"20221020190229529","modified":"20221023200524937","tags":"","type":"text/vnd.tiddlywiki","text":"<div class="twg-example">

