        "$:/plugins/kookma/section/styles/details/divided-sections": {
            "title": "$:/plugins/kookma/section/styles/details/divided-sections",
            "text": ".se-section details[open] summary ~ * {
	animation: open 0.3s ease-in-out;
}

@keyframes open {
	0%   {opacity: 0;}
	100% {opacity: 1;}
}
.se-section details summary::-webkit-details-marker {
	display: none;
}

.se-section details summary {
	width: 100%;
	padding: 0.5rem 0;
	border-top: 1px solid #ccc;
	position: relative;
	cursor: pointer;
	/*	font-size: 1.25rem; 
	font-weight: 300; */
	list-style: none;
}

.se-section details summary:after {
	content: "+";
	/*	color: black;*/
	position: absolute;
	font-size: 1.75rem;
	line-height: 0;
	margin-top: 0.75rem;
	right: 0;
	font-weight: 200;
	transform-origin: center;
	transition: 200ms linear;
}
.se-section details[open] summary:after {
	transform: rotate(45deg);
	font-size: 2rem;
}
.se-section details summary {
	outline: 0;
}
/*
.se-section details p {
font-size: 0.95rem;
margin: 0 0 1rem;
padding-top: 1rem;
}
*/

/* by MIM*/
.se-section details summary h1,
.se-section details summary h2,
.se-section details summary h3,
.se-section details summary h4{
	/*font-size: 1.5rem; 
	font-weight: 300; */
}",
