<$button class="tc-btn-invisible" tooltip="add comment" actions={{$:/plugins/kookma/thinkup/comments/add-comment-button-actions}}>
{{$:/plugins/kookma/thinkup/images/add-comment}}
</$button>
</$reveal>
"},"$:/plugins/kookma/thinkup/comments/comments-template":{"title":"$:/plugins/kookma/thinkup/comments/comments-template","created":"20211029124219377","modified":"20211112095801056","tags":"show-content","type":"text/vnd.tiddlywiki","text":"<div class="tc-comments">
<div class="tc-comment-list"> <!--ol-->
<$list filter="[all[tiddlers+shadows]role[comment]contains<currentTiddler>sort[created]!has[draft.of]]">
<div> <!--li-->
<div class="tc-comment-entry">
<div class="tc-comment-entry-heading">
<$link>{{!!creator}} at <$view field="modified" format="date" template="0hh:0mm:0ss DDD DDth MMM YYYY"/></$link>
<$list filter="[all[shadows+tiddlers]tag[$:/tags/Thinkup/CommentsToolbarButton]!has[draft.of]]" variable="listItem">
<$transclude tiddler=<<listItem>> mode="inline"/>
</$list>
</div>
<div class="tc-comment-entry-body">
<$reveal type="match" state="!!edit-mode" text="yes">
<!-- use simple text area
<$edit-text tiddler=<<currentTiddler>> tag="textarea" focus="true"/>
--><!-- use the main editor like codeMirror -->
<!-- keywords are added by Mohammad, check the buttons -->
<$keyboard actions="""<$action-setfield $tiddler=<<currentTiddler>> $field="edit-mode" $value="no"/>""" key="ctrl+enter">
<$keyboard actions="""<$action-setfield $tiddler=<<currentTiddler>> $field="edit-mode" $value="no"/><$action-setfield $tiddler=<<currentTiddler>> $field="text" $value={{!!saved-text}}/>""" key="escape">
<$transclude tiddler="$:/core/ui/EditTemplate/body" mode=block/>
</$keyboard>
</$keyboard>
</$reveal>
<$reveal type="nomatch" state="!!edit-mode" text="yes">
<$transclude tiddler=<<currentTiddler>> mode="block"/>
<!-- <$transclude tiddler="$:/plugins/tiddlywiki/comments/add-comment-button" mode="inline"/>  -->
</$reveal>
</div>
</div>
<!-- <$transclude tiddler="$:/plugins/kookma/thinkup/comments/comments-template" mode="inline"/> -->
</div> <!--li-->
</$list>
</div><!--ol-->
</div>"},"$:/plugins/kookma/thinkup/comments/filter-all-comments":{"title":"$:/plugins/kookma/thinkup/comments/filter-all-comments","created":"20211029122811467","description":"All comments","filter":"[role[comment]!sort[modified]]","modified":"20211102110650145","tags":"$:/tags/Filter","type":"text/vnd.tiddlywiki"},"$:/plugins/kookma/thinkup/comments/header-view-template-segment":{"title":"$:/plugins/kookma/thinkup/comments/header-view-template-segment","created":"20211029132237328","modified":"20211217092150604","tags":"$:/tags/ViewTemplate","type":"text/vnd.tiddlywiki","text":"\define display-original-comment()
<$link><$text text=<<currentTiddler>>/></$link>
\end

\define find-original-comment(exclude)
<$list filter="[<currentTiddler>role[comment]]" emptyMessage=<<display-original-comment>> variable="ignore">
<$list filter="[list<currentTiddler>sort[title]] -[enlist<__exclude__>]">
<$set name="newExclude" filter="[enlist<__exclude__>] [<currentTiddler>]">
<$macrocall $name="find-original-comment" exclude=<<newExclude>>/>
</$set>
</$list>
</$list>
\end

<$list filter="[all[current]role[comment]]" variable="ignore">
<div class="tc-is-comment-header">
This tiddler is a comment on
<$list filter="[list<currentTiddler>sort[title]]">
<span class="tc-small-gap-right"><<find-original-comment>></span>
</$list>
<$list filter="[list<currentTiddler>role[comment]sort[title]limit[1]]" variable="ignore">
<p>
Parent comments:
</p>
<ul>
<$list filter="[list<currentTiddler>role[comment]sort[title]]">
<li>
<$link to=<<currentTiddler>>><$text text=<<currentTiddler>>/></$link>
</li>
</$list>
</ul>
</$list>
</div>
</$list>
"},"$:/plugins/kookma/thinkup/comments/sidebar":{"title":"$:/plugins/kookma/thinkup/comments/sidebar","caption":"Comments","created":"20211029131237923","modified":"20211112064213900","tags":"show-content","type":"text/vnd.tiddlywiki","text":"<div class="tc-timeline">
<$list filter="[all[tiddlers+shadows]role[comment]has[modified]!sort[modified]eachday[modified]]">
<div class="tc-menu-list-item">
<$view field="modified" format="date" template="DDth MMM YYYY"/>
<$list filter="[all[tiddlers+shadows]role[comment]sameday:modified{!!modified}!sort[modified]]">
<div class="tc-menu-list-subitem">
<$link>Comment by '<$view field="modifier">(anonymous)</$view>'</$link> on
<$list filter="[list<currentTiddler>sort[title]]">
<$link to=<<currentTiddler>>><$text text=<<currentTiddler>>/></$link>
</$list>
</div>
</$list>
</div>
</$list>
</div>
"},"$:/plugins/kookma/thinkup/comments/styles":{"title":"$:/plugins/kookma/thinkup/comments/styles","created":"20211029130710247","modified":"20211112055151270","tags":"$:/tags/Stylesheet","type":"text/vnd.tiddlywiki","text":"\rules only filteredtranscludeinline transcludeinline macrodef macrocallinline


.tc-is-comment-header {
	padding:0.25em;
  border-radius: 4px;
/*	border: 2px solid #c1e1ea;
	background: #f1fcff; */
	border: 2px solid <<color tab-border>>;
	background: <<color tiddler-editor-background>>;
}

/*
.tc-comments-segment {
	border-top: 2px solid #d7eef4;
}
*/

.tc-comment-button button {
	width: 100%;
	text-align: right;
}

.tc-sidebar-scrollable .tc-comment-button button {
	width: auto;
	text-align: right;
}


.tc-comment-button button svg {
/*	fill: #26cb56;*/
   fill: <<color tiddler-controls-foreground>>;
   height: 1.5em;  /* by mohammad */
   width: 1.5em;
}

/* Mohammad --------------*/
.tc-comment-button:hover button svg{
fill: <<color tiddler-controls-foreground-hover>> ;
}


.tc-comments {}

.tc-comment-list {
	list-style: none;
     padding-left: 0;
}

.tc-comment-list .tc-comments {
	padding-left: 1em;
}

.tc-comment-entry {
	position: relative;
	border-radius: 4px;
	margin: 0.5em 0 0 0;
/* background: #f1fcff;*/
/* border: 2px solid #c1e1ea; */
	border: 2px solid <<color tab-border>>;
	background: <<color tiddler-editor-background>>;
}

.tc-comment-entry-heading {
	font-size: 0.7em;
	font-weight: bold;
	text-transform: uppercase;
/*	background: #d7eef4;
	color: #5B6D80; */
	background: <<color tab-background>>;
  color: <<color tab-foreground>>;
	padding: 0 0.5em;
}

.tc-comment-entry-body {
	font-size: 0.8em; /* affects view mode and toolbars in edit mode */
	line-height:1.25em;
	padding: 0 0.5em;
}

/* font size of comment editor */
.tc-comment-entry-body textarea {
	font-size: 0.9em;
	width: 100%;
  min-height: 40px;
  resize: vertical; /*  user can resize vertically, but width is fixed */
}

.tc-comment-entry-body .CodeMirror { /* if CodeMirror is in use */
	font-size: 1.2em;
}"},"$:/plugins/kookma/thinkup/comments/toolbar-button-cancel":{"title":"$:/plugins/kookma/thinkup/comments/toolbar-button-cancel","created":"20211029121235226","modified":"20211029121235226","tags":"$:/tags/Thinkup/CommentsToolbarButton","type":"text/vnd.tiddlywiki","text":"<$reveal state="$:/status/IsReadOnly" type="match" text="no" default="no" tag="span">
