<<<"},"$:/plugins/kookma/thinkup/macros/definition":{"title":"$:/plugins/kookma/thinkup/macros/definition","created":"20210803141955154","modified":"20211117100658126","tags":"show-content","type":"text/vnd.tiddlywiki","text":"\define tidNodeExplorerConfig() $:/config/Thinkup/NodeExplorer
\define tidStabsConfig() $:/config/Thinkup/Stabs/EnableFilter
\define tidJournalConfig() $:/config/Thinkup/Journal/EnableFilter


\define tidExcludeStabsConfig() $:/config/Thinkup/Stabs/ExcludeFilter
\define tidExcludeCtabsConfig() $:/config/Thinkup/Ctabs/ExcludeFilter
\define tidShowSearchCategoryTab() $:/config/Thinkup/Ctabs/EnableSearch
\define tidSearchinCtab() $:/temp/thinkup/ctabs-search/$(currentTiddler)$"},"$:/plugins/kookma/thinkup/macros/node-explorer":{"title":"$:/plugins/kookma/thinkup/macros/node-explorer","created":"20210801192651059","modified":"20211109051323911","tags":"show-content","type":"text/vnd.tiddlywiki","text":"\import $:/plugins/kookma/thinkup/macros/definition

\define xtext-pattern-transclude() {{.*}}

\define text-pattern-transclude()
{{$(currentTiddler)$.*}}
\end

\define linksFilter()         [all[current]!is[system]links[]]
\define backlinksFilter()     [all[current]!is[system]backlinks[]]
\define tagsFilter()          [all[current]!is[system]tagging[]]
\define transclusionFilter()  [regexp:text<text-pattern-transclude>]

<$set name=mainfilter filter="[<tidNodeExplorerConfig>getindex[links]match[yes]then<linksFilter>] [<tidNodeExplorerConfig>getindex[backlinks]match[yes]then<backlinksFilter>] [<tidNodeExplorerConfig>getindex[tagging]match[yes]then<tagsFilter>] [<tidNodeExplorerConfig>getindex[transclusion]match[yes]then<transclusionFilter>]">

<!--check to see if there is any node -->
<$list filter="[subfilter<mainfilter>] +[count[]compare:number:gteq[1]]" variable=null>

<!-- change the srcClass and labelClass to change the feel and look of Node Explorer. For customizing dynamic table, see $:/plugins/kookma/thinkup/ui/node-explorer
-->

<$transclude tiddler="$:/plugins/kookma/thinkup/ui/node-explorer" />

</$list>
</$set>"},"$:/plugins/kookma/thinkup/macros/people":{"title":"$:/plugins/kookma/thinkup/macros/people","created":"20211030190945011","modified":"20211030192613094","tags":"$:/tags/Macro","type":"text/vnd.tiddlywiki","text":"\define pcontact(
email:"not-passed",
webpage:"not-passed",
photo:"not-passed",
research-gate:"not-passed",
google-scholar:"not-passed"
)
<table class="table-borderless">
<$list filter="email webpage research-gate google-scholar" variable=currentItem>
<$list filter="[<currentItem>trim[]!is[blank]]" variable=null>
<tr><td><<currentItem>>
</$list>
</$list>
\end

<<pcontact
  email:"m.rahmani@aut.ac.ir"
  webpage:"https://aut.ac.ir"
	research-gate:""
  google-scholar:""
	photo:""
	photo-caption:""
>>"},"$:/plugins/kookma/thinkup/macros/searchbox":{"title":"$:/plugins/kookma/thinkup/macros/searchbox","created":"20211108080705516","modified":"20211113160744710","tags":"show-content","type":"text/vnd.tiddlywiki","text":"\define search-box(tempTiddler)
<$keyboard key="escape" actions="""<$action-setfield $tiddler=<<__tempTiddler__>> $field=text $value=""/>""" >
<$edit-text tiddler=<<__tempTiddler__>>
  field=text 	tag=input 	type=search
	default=""  placeholder=" enter your search terms"
	class="search-box" />
</$keyboard>
\end"},"$:/plugins/kookma/thinkup/macros/stabs":{"title":"$:/plugins/kookma/thinkup/macros/stabs","created":"20211028173456201","modified":"20211112063835757","tags":"show-content","type":"text/vnd.tiddlywiki","text":"\define make-caption() <$transclude tiddler={{$(currentTiddler)$!!icon}} mode=inline/>
\define hide-tabs-button()
<$list filter="[<currentTab>!is[blank]]">
<$button class="tc-btn-invisible tab-button"  actions="""<$action-setfield $tiddler=<<tempdataTid>> text="" />""">{{$:/plugins/kookma/thinkup/images/close}}</$button>
</$list>
\end

\define thinkup-stabs(filter)
<section class="thinkup-tab-set">
<$vars tempdataTid="$:/temp/thinkup-tabs/$(currentTiddler)$">
<!-- Tab links -->
<div class="thinkup-tab-buttons">
<$vars currentTab= {{{ [<tempdataTid>get[text]] }}} >
<<hide-tabs-button>>
<$list filter=<<__filter__>> >
<$vars btnClass= {{{ [<currentTiddler>match<currentTab>then[tc-btn-invisible tab-button selected]else[tc-btn-invisible tab-button]] }}} > 
<$button  class=<<btnClass>> tooltip={{{[<currentTiddler>get[tooltip]]}}}  actions="""<$action-setfield $tiddler=<<tempdataTid>> text=<<currentTiddler>> />"""  ><<make-caption>></$button>
</$vars>
</$list>
</$vars>
</div>
<!----- create the divider: for future...
<div class="thinkup-tab-divider "></div> 
----->
<!-- Tab content -->
<$vars currentTab= {{{ [<tempdataTid>get[text]] }}} >
<$list filter="[subfilter<__filter__>match<currentTab>] ~[subfilter<__filter__>first[]]" variable=tab>
<$vars contentStyle= {{{ [<tab>match<currentTab>then[display:block;]] }}} > 
<div class="tab-content" style=<<contentStyle>> > 
  <$transclude tiddler=<<tab>> mode=block/>
</div>
</$vars>
</$list>
</$vars>
</$vars>

</section>
\end"},"$:/plugins/kookma/thinkup/macros/tag-cloud":{"title":"$:/plugins/kookma/thinkup/macros/tag-cloud","created":"20210831144029397","modified":"20211102190815667","tags":"show-content","type":"text/vnd.tiddlywiki","text":"\define getTags()
<$list filter="[all[tiddlers]!prefix[$:/]tags[]] +[!prefix[$:/]] $(extra_filter)$">
   <$text text="[["/>{{{ [<currentTiddler>tagging[]count[]] }}}°≈°<<currentTiddler>><$text text="]]"/>
</$list>
\end

\define sortTags()
<$list filter="[enlist<items>!sortan[]] :filter[split[°≈°]first[]compare:integer:gteq<__n__>]" >
   <$list filter="[<currentTiddler>split[°≈°]last[]]"><$text text="[["/><<currentTiddler>><$text text="]]"/></$list>
</$list>
\end

\define tagcloud_showbycount(n:5, extrafilter:"")
<$vars extra_filter=<<__extrafilter__>> >
<$wikify    name="items"  text=<<getTags>>  >
   <$wikify name="sorted" text=<<sortTags>> >
      <$list filter=<<sorted>>>
         <$vars count={{{ [<currentTiddler>tagging[]count[]] }}}>
          <span class="kk-tu-sp"><<tag>><sup class="kk-tu-sup"><<count>></sup></span>
         </$vars>
      </$list>
   </$wikify>
</$wikify>
</$vars>
\end

<!--
;Original idea
: Eric Shulman
:http://tiddlytools.com/filtergenerators.html#TiddlyTools%2FFilterGenerators%2FTagCloud
-->

<!-- 2021.05.21
Mohammad
[FIXED] both system tags and system tiddlers are excluded now!
[NEW] only tags with specified number (frequency used)is displayed
[NEW] extrafilter parameter like `+[has[color]]` or `-[prefix[exmp]]` can be passed to refine the tags result (this apply on tgas not on tiddlers tagged with something). IMPORTANT: `+` and `-` are mandatory!
-->"},"$:/plugins/kookma/thinkup/macros/tinyurl":{"title":"$:/plugins/kookma/thinkup/macros/tinyurl","created":"20211118044823660","modified":"20211118050611724","tags":"$:/tags/Macro","type":"text/vnd.tiddlywiki","text":"\define tinyurl(address, label:"",  n:3)
