<$list filter="[all[current]tag[Journal]]">
<div style="margin-bottom:40px">

<$macrocall $name=interstitial-ui base=<<currentTiddler>> caption="Interstitial Journals"/>

</div>
</$list>



<$vars stabsFilter={{{[<tidJournalConfig>get[text]]}}}>
<$list filter="[subfilter<stabsFilter>match<currentTiddler>limit[1]] +[!is[missing]]" variable="ignore">
<$macrocall $name=thinkup-stabs filter="[all[tiddlers+shadows]tag[$:/tags/Thinkup/SmartTab/Journal]!has[draft.of]]" />
</$list>
</$vars>

