\define showField() 
<$list filter="[all[current]has<field>]" variable=null>
<tr><th><$text text=<<field>> /></th><td><$transclude field=<<field>> /></td></tr>
</$list>
\end

<$list filter="[all[current]tag[Source]]">
<table class="table-borderless source">
