"},"$:/plugins/kookma/timelines/macros/ilam/left-pos":{"title":"$:/plugins/kookma/timelines/macros/ilam/left-pos","tags":"$:/tags/Timelines/Ilam","type":"text/vnd.tiddlywiki","text":"\define __tlIlam-ItemSpace() 120 <!-- the space at the left of each item used to calculate the position of items-->
\define __tlIlam-EventLength()
<!--this calculate the extra pixels required for events date appears on the timeline. Each format has a certain width which has to be added to calculate the length of filling line -->

<$list filter="[<__dateformat__>match[YYYY]]" variable=ignore>16</$list>
<$list filter="[<__dateformat__>match[0DD mmm YYYY]]" variable=ignore>32</$list>
<$list filter="[<__dateformat__>match[mmm DDth]]" variable=ignore>20</$list>
\end


\define __tlIlam-LeftPosition() 
<!--calculates the left position of each item -->
<$list filter="[list<container>allbefore:include<currentTiddler>count[]multiply<__tlIlam-ItemSpace>]" variable="leftpos">
left:<<leftpos>>px;
</$list>
\end

\define __tlIlam-LineLength() 
<!--calculates the length of filling line -->
<!-- number of items displayed on screen: 3 -->

<$wikify name=txt-length text=<<__tlIlam-EventLength>> >
<!-- selected item is in the container list -->
<$list filter="[list<container>allbefore:include<selected-item>limit[1]]">
<$set name=length filter="[list<container>allbefore:include<selected-item>count[]multiply<__tlIlam-ItemSpace>add<txt-length>]">
width: <<length>>px;
</$set>
</$list>

<!-- container items are after selected item -->
<$set name=last-item filter="[list<container>last[]]" >
<$list filter="[subfilter<__filter__>allafter<last-item>match<selected-item>]">
<$set name=length filter="[[3]multiply<__tlIlam-ItemSpace>add<txt-length>]">
width: <<length>>px;
</$set>
</$list>
</$set>

</$wikify>
\end"},"$:/plugins/kookma/timelines/macros/ilam/pull-next":{"title":"$:/plugins/kookma/timelines/macros/ilam/pull-next","tags":"$:/tags/Timelines/Ilam","type":"text/vnd.tiddlywiki","text":"\define __tlIlam-PullNext()
<!-- number of items displayed on screen: 3 -->
<$vars
 first-item={{{ [list<container>first[1]] }}}
 last-item={{{ [list<container>last[1]] }}}
>

<$action-setfield $tiddler=<<container>> list=""/>
<$list filter="[enlist<filter>allafter:include<last-item>first[3]count[]] -1 -2 ~[[::false]]" variable=result>
   <$list filter="[<result>prefix[::false]]" variable=ignore>
        <$action-listops $tiddler=<<container>> $filter="[enlist<filter>last[3]]"/>
   </$list>
   <$list filter="[<result>!prefix[::false]]" variable=case>
        <$action-listops $tiddler=<<container>> $filter="[enlist<filter>allafter:include<last-item>first[3]] ~[enlist<filter>last[3]]"/>  
   </$list> 
</$list>

</$vars>
\end
"},"$:/plugins/kookma/timelines/macros/ilam/pull-prev":{"title":"$:/plugins/kookma/timelines/macros/ilam/pull-prev","tags":"$:/tags/Timelines/Ilam","type":"text/vnd.tiddlywiki","text":"\define __tlIlam-PullPrevious()
