"},"$:/plugins/kookma/timelines/macros/sequence-horizontal":{"title":"$:/plugins/kookma/timelines/macros/sequence-horizontal","tags":"$:/tags/Macro","type":"text/vnd.tiddlywiki","text":"\define sequence-horizontal(filter, limit:10, template:"", footer:"footer", class:"primary", date:"modified", stateTiddler)
<!-- sequence-horizontal with Tehran code name creates a horizontal sequence -->

<!-- this is a nested macro -->
\define __tlTehran-changeit() <$action-setfield $tiddler=<<container>> text=<<entry>>/>

<$let container     = <<qualify """$:/state/tl/tehran-$stateTiddler$""">> 
      selected-item = {{{[<container>get[text]] ~[subfilter<__filter__>first[]]}}}   >

<div class="kk-tl-tehran kk-tl-tehran-$class$">

<$set name=templateTiddler value=<<__template__>> emptyValue="$:/plugins/kookma/timelines/templates/tehran" >
<div class="kk-tl-tehran-content"><!-- display content of selected tiddler -->
<$list filter="[<container>get[text]] ~[<selected-item>]" template=<<templateTiddler>> />
</div>
</$set>

<ul>
<$list filter="[subfilter<__filter__>limit[$limit$]]" variable="entry"><!-- main list -->
<$list filter="[<entry>match<selected-item>] ~[[::not-selected]]" variable=choice>
<$list filter="[<choice>match<selected-item>]">
<li class="kk-tl-tehran-active">
<$button class="tc-btn-invisible" actions=<<__tlTehran-changeit>> >
<$count filter='[subfilter<__filter__>allbefore:include<entry>]'/>
</$button>
</li>
</$list>
<$list filter="[<choice>match[::not-selected]]">
<li>
<$button class="tc-btn-invisible" actions=<<__tlTehran-changeit>> >
<$count filter='[subfilter<__filter__>allbefore:include<entry>]'/>
</$button>
</li>
</$list>
</$list>
</$list>
</ul>

</div>
</$let>
\end


"},"$:/plugins/kookma/timelines/macros/sequence-vertical":{"title":"$:/plugins/kookma/timelines/macros/sequence-vertical","tags":"$:/tags/Macro","type":"text/vnd.tiddlywiki","text":"\define sequence-vertical(filter, limit:10, template:"", footer:"footer", class, date:"modified")
<!-- The code name for sequence-vertical is gilan -->
<$set name=templateTiddler value=<<__template__>> emptyValue="$:/plugins/kookma/timelines/templates/gilan" >
<div class="kk-tl-gilan">

<$list filter="[subfilter<__filter__>limit[$limit$]]" emptyMessage="Timeline: no tiddler found or wrong filter is passed!" >
<div class="item kk-tl-gilan-$class$"> <!--display content-->

<$transclude tiddler=<<templateTiddler>> mode="block"/>

</div>
</$list>

</div>
</$set>
\end
"},"$:/plugins/kookma/timelines/macros/timeline-horizontal":{"title":"$:/plugins/kookma/timelines/macros/timeline-horizontal","tags":"$:/tags/Macro","type":"text/vnd.tiddlywiki","text":"\define timeline-horizontal(filter, limit:10, template:"", footer:"footer", class, date:"modified", stateTiddler:"", dateformat:"YYYY")
\import [all[tiddlers+shadows]tag[$:/tags/Timelines/Ilam]]

<!-- timeline-horizontal with Ilam as code name creates a horizontal timeline -->

<!-- dateformat here is a hidden argument. It can be changed to "0DD mmm YYYY" if the eventlength is set to 32.
See $:/plugins/kookma/timelines/macros/ilamleft-pos -->

\define __tlIlam-changeit() <$action-setfield $tiddler=<<container>> text=<<currentTiddler>>/>

<$set name=filter filter="[subfilter<__filter__>] :sort:date[get<__date__>] :and[limit[$limit$]]">
<$let container=<<qualify """$:/state-tl/ilam-$stateTiddler$""">>  
      selected-item={{{[<container>get[text]] ~[enlist<filter>first[]]}}} >

<div class="kk-tl-ilam kk-tl-ilam-container">
<div class="dates">
<div class="line">

<ol>

<$list filter="[list<container>]" variable="entry"> <!-- main list -->

<$list filter="[enlist<filter>allbefore<selected-item>match<entry>]"><!-- all before selected item -->
<$wikify name="stleft" text=<<__tlIlam-LeftPosition>> >
<li>
<$button class="tc-btn-invisible date date--older-event" style=<<stleft>> actions=<<__tlIlam-changeit>> >
<<__tlIlam_ShowEventDate>>
</$button>
</li>
</$wikify>
</$list>

<$list filter="[<entry>match<selected-item>]"><!-- the selected item -->
<$wikify name="stleft" text=<<__tlIlam-LeftPosition>> >
<li>
<$button class="tc-btn-invisible date date--selected" style=<<stleft>> actions=<<__tlIlam-changeit>> >
<<__tlIlam_ShowEventDate>> 
</$button>
</li>
</$wikify>
</$list>

<$list filter="[enlist<filter>allafter<selected-item>match<entry>]"><!-- all after selected item -->
<$wikify name="stleft" text=<<__tlIlam-LeftPosition>> >
<$button class="tc-btn-invisible date" style=<<stleft>> >
<<__tlIlam-changeit>><<__tlIlam_ShowEventDate>> 
</$button>
</$wikify>
</$list>

</$list><!--- main list -->

</ol>

<$wikify name="stlength" text=<<__tlIlam-LineLength>> ><!-- the filling line -->
<span class="filling-line" style=<<stlength>> aria-hidden="xtrue"></span>
</$wikify>

</div><!--line-->
</div><!-- dates-->

<ul><!-- arrow btns-->
<li>
<$button class="tc-btn-invisible text-replace navigation navigation--prev" actions=<<__tlIlam-PullPrevious>> >
Prev
</$button>
</li>
<li>
<$button class="tc-btn-invisible text-replace navigation navigation--next" actions=<<__tlIlam-PullNext>> >
Next
</$button>
</li>
</ul>
</div><!-- container-->

<$set name=templateTiddler value=<<__template__>> emptyValue="$:/plugins/kookma/timelines/templates/ilam" >
<div class="kk-tl-ilam-content kk-tl-ilam-$class$"><!--display contents-->
<$tiddler tiddler=<<selected-item>> >
<$transclude tiddler=<<templateTiddler>> mode="block"/>
</$tiddler>
</div>
</$set>

</$let>
</$set>
\end
"},"$:/plugins/kookma/timelines/macros/timeline-vertical":{"title":"$:/plugins/kookma/timelines/macros/timeline-vertical","tags":"$:/tags/Macro","type":"text/vnd.tiddlywiki","text":"\define timeline-vertical(filter, limit:10, template:"", footer:"footer", class:"light", date:"modified", dateformat:"mmm 0DDth YYYY")
<!-- Kerman -->
<$set name=templateTiddler value=<<__template__>> emptyValue="$:/plugins/kookma/timelines/templates/kerman" >
<div class="kk-tl-kerman kk-tl-kerman-$class$">
<ul>
<$list filter="[subfilter<__filter__>] :sort:date[get<__date__>] :and[limit[$limit$]]" emptyMessage="Timeline: no tiddler found or wrong filter is passed!" >
<li>
<span></span><!--display content-->

<$transclude tiddler=<<templateTiddler>> mode="block"/>

<div class="item-timedate">
<span><<____tlkerman_ShowEventDate-month>></span>
<span><<____tlkerman_ShowEventDate-year>></span>
</div>
</li>
</$list>
</ul>
</div>
\end

\define ____tlkerman_ShowEventDate-year()
<$list filter="[<__dateformat__>search[YYYY]]" variable=ignore>
<$view field=<<__date__>> format=date template="YYYY"/>
</$list>
\end

\define ____tlkerman_ShowEventDate-month()
